function createDateReferences() {
    try {
        // Utility function to safely create dates and handle invalid dates
        function createSafeDate(...args) {
            const date = new Date(...args);
            if (isNaN(date.getTime())) {
                throw new Error(`Invalid date created with args: ${JSON.stringify(args)}`);
            }
            return date;
        }

        const now = createSafeDate();
        const pstOptions = { timeZone: 'America/Los_Angeles' };

        // Enhanced format function with error handling
        function formatDate(date) {
            try {
                if (!date || isNaN(date.getTime())) {
                    throw new Error('Invalid date provided to formatDate');
                }
                return date.toLocaleDateString('en-US', {
                    weekday: 'long',
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric',
                    ...pstOptions
                });
            } catch (error) {
                console.error(`Error formatting date: ${error.message}`);
                return 'Invalid Date';
            }
        }

        // Safe date manipulation with error handling
        function manipulateDate(date, manipulation) {
            try {
                if (!date || isNaN(date.getTime())) {
                    throw new Error('Invalid base date for manipulation');
                }
                const newDate = createSafeDate(date);
                manipulation(newDate);
                if (isNaN(newDate.getTime())) {
                    throw new Error('Date manipulation resulted in invalid date');
                }
                return newDate;
            } catch (error) {
                console.error(`Date manipulation error: ${error.message}`);
                return createSafeDate(); // Return current date as fallback
            }
        }

        // Handle month transitions safely
        function getMonthBoundaries(yearOffset = 0, monthOffset = 0) {
            try {
                const year = now.getFullYear() + yearOffset;
                const month = now.getMonth() + monthOffset;

                // Normalize month and year if we overflow
                const normalizedYear = year + Math.floor(month / 12);
                const normalizedMonth = ((month % 12) + 12) % 12; // Handle negative months

                const start = createSafeDate(normalizedYear, normalizedMonth, 1);
                const end = createSafeDate(normalizedYear, normalizedMonth + 1, 0);

                return { start, end };
            } catch (error) {
                console.error(`Error calculating month boundaries: ${error.message}`);
                return {
                    start: createSafeDate(),
                    end: createSafeDate()
                };
            }
        }

        // Get week boundaries with error handling for DST transitions
        function getWeekBoundaries(weekOffset = 0) {
            try {
                const startOfWeek = manipulateDate(now, d => {
                    d.setDate(d.getDate() - d.getDay() + (weekOffset * 7));
                    d.setHours(0, 0, 0, 0);
                });

                const endOfWeek = manipulateDate(startOfWeek, d => {
                    d.setDate(d.getDate() + 6);
                    d.setHours(23, 59, 59, 999);
                });

                return { start: startOfWeek, end: endOfWeek };
            } catch (error) {
                console.error(`Error calculating week boundaries: ${error.message}`);
                return {
                    start: createSafeDate(),
                    end: createSafeDate()
                };
            }
        }

        // Safe time formatting with DST handling
        function formatTime(date) {
            try {
                if (!date || isNaN(date.getTime())) {
                    throw new Error('Invalid date provided to formatTime');
                }
                return date.toLocaleTimeString('en-US', {
                    hour: '2-digit',
                    minute: '2-digit',
                    second: '2-digit',
                    hour12: true,
                    ...pstOptions
                });
            } catch (error) {
                console.error(`Error formatting time: ${error.message}`);
                return 'Invalid Time';
            }
        }

        // Determine timezone with error handling
        function getTimezoneIndicator() {
            try {
                const pstDate = new Date().toLocaleString('en-US', pstOptions);
                return pstDate.includes('PDT') ? 'PDT' : 'PST';
            } catch (error) {
                console.error(`Error determining timezone: ${error.message}`);
                return 'PST'; // Default to PST if there's an error
            }
        }

        const thisWeek = getWeekBoundaries(0);
        const lastWeek = getWeekBoundaries(-1);
        const nextWeek = getWeekBoundaries(1);

        const thisMonth = getMonthBoundaries(0, 0);
        const lastMonth = getMonthBoundaries(0, -1);
        const nextMonth = getMonthBoundaries(0, 1);

        const thisYear = {
            start: createSafeDate(now.getFullYear(), 0, 1),
            end: createSafeDate(now.getFullYear(), 11, 31)
        };
        const lastYear = {
            start: createSafeDate(now.getFullYear() - 1, 0, 1),
            end: createSafeDate(now.getFullYear() - 1, 11, 31)
        };
        const nextYear = {
            start: createSafeDate(now.getFullYear() + 1, 0, 1),
            end: createSafeDate(now.getFullYear() + 1, 11, 31)
        };

        const formattedTime = formatTime(now);
        const timezoneIndicator = getTimezoneIndicator();

        return {
            json: {
                current: {
                    dateTime: `${formatDate(now)} at ${formattedTime} ${timezoneIndicator}`,
                    date: formatDate(now),
                    time: `${formattedTime} ${timezoneIndicator}`,
                    timezone: timezoneIndicator,
                    timestamp: now.getTime() // Include timestamp for additional reference
                },
                relative: {
                    yesterday: formatDate(manipulateDate(now, d => d.setDate(d.getDate() - 1))),
                    today: formatDate(now),
                    tomorrow: formatDate(manipulateDate(now, d => d.setDate(d.getDate() + 1)))
                },
                weeks: {
                    thisWeek: {
                        start: formatDate(thisWeek.start),
                        end: formatDate(thisWeek.end)
                    },
                    lastWeek: {
                        start: formatDate(lastWeek.start),
                        end: formatDate(lastWeek.end)
                    },
                    nextWeek: {
                        start: formatDate(nextWeek.start),
                        end: formatDate(nextWeek.end)
                    }
                },
                months: {
                    thisMonth: {
                        start: formatDate(thisMonth.start),
                        end: formatDate(thisMonth.end)
                    },
                    lastMonth: {
                        start: formatDate(lastMonth.start),
                        end: formatDate(lastMonth.end)
                    },
                    nextMonth: {
                        start: formatDate(nextMonth.start),
                        end: formatDate(nextMonth.end)
                    }
                },
                years: {
                    thisYear: {
                        start: formatDate(thisYear.start),
                        end: formatDate(thisYear.end)
                    },
                    lastYear: {
                        start: formatDate(lastYear.start),
                        end: formatDate(lastYear.end)
                    },
                    nextYear: {
                        start: formatDate(nextYear.start),
                        end: formatDate(nextYear.end)
                    }
                }
            }
        };
    } catch (error) {
        // Global error handler
        console.error('Critical error in date reference creation:', error);
        // Return a minimal valid response with current date/time
        const fallbackDate = new Date();
        return {
            json: {
                error: true,
                errorMessage: error.message,
                current: {
                    dateTime: fallbackDate.toISOString(),
                    date: fallbackDate.toDateString(),
                    time: fallbackDate.toTimeString(),
                    timezone: 'PST'
                }
            }
        };
    }
}

return JSON.stringify(createDateReferences());
