// This function will be used in an n8n Code node
function processData(items) {
  // Process each item from the incoming data
  for (const item of items) {
    // Check if contentSnippet exists and clean it
    if (item.json.contentSnippet) {
      item.json.contentSnippet = cleanHtmlAndCss(item.json.contentSnippet);
    }

    // Check if content exists and clean it
    if (item.json.content) {
      item.json.content = cleanHtmlAndCss(item.json.content);
    }
  }

  return items;
}

// Helper function to clean HTML and CSS content
function cleanHtmlAndCss(text) {
  // First remove style and script tags with their content
  text = text.replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
             .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '');

  // Remove CSS selector lines (pattern: .selector or #selector)
  text = text.replace(/\s*[.#][a-zA-Z0-9_-]+(\s+[a-zA-Z0-9_>,.#:\[\]="'\*\(\)^-]+)*\s*(\{[^}]*\})?/g, ' ');

  // Remove CSS rules that may be outside of style tags
  text = text.replace(/\s*\{[^}]*\}/g, ' ');

  // Remove at-rules like @font-face, @media
  text = text.replace(/@[a-zA-Z-]+[^{]*\{[\s\S]*?\}/gi, ' ');
  text = text.replace(/@[a-zA-Z-]*\s[^;]*;/g, ' ');

  // Remove HTML tags
  text = text.replace(/<[^>]*>/g, ' ');

  // Replace common HTML entities
  text = text.replace(/&nbsp;/g, ' ')
             .replace(/&amp;/g, '&')
             .replace(/&lt;/g, '<')
             .replace(/&gt;/g, '>')
             .replace(/&quot;/g, '"')
             .replace(/&#39;/g, "'");

  // Remove CSS property specifications (like color: red;)
  text = text.replace(/[a-zA-Z-]+\s*:\s*[^;)]*[;)]/g, ' ');

  // Remove CSS function calls like format('woff2')
  text = text.replace(/format\([^)]*\)/g, ' ');

  // Remove remaining CSS-looking content
  text = text.replace(/url\([^)]*\)/g, ' ');

  // Remove HTML element names on their own line
  text = text.replace(/^(html|body|div|span|h[1-6]|p|a|img|table|tr|td|th|ul|ol|li|sup)$/gm, ' ');

  // Remove CSS class and ID selectors that might be on their own line
  text = text.replace(/^[.#][a-zA-Z0-9_-]+$/gm, ' ');

  // Remove pseudo elements and classes
  text = text.replace(/:[a-zA-Z-]+/g, ' ');

  // Remove media query fragments
  text = text.replace(/@media\s+[^{]+/g, ' ');

  // Remove any lines that look like CSS selector combinations
  text = text.replace(/^[a-zA-Z0-9_,.\s#>+~:[\]="'*()-]+$/gm, ' ');

  // Remove empty braces
  text = text.replace(/\{\s*\}/g, ' ');

  // Extra cleanup for common patterns in your example
  text = text.replace(/:\s*root/g, ' ')
             .replace(/\}\s*@media/g, ' ')
             .replace(/screen\s+and\s+\([^)]*\)/g, ' ');

  // Clean up multiple spaces, newlines, etc.
  text = text.replace(/\s+/g, ' ').trim();

  // Filter out only text that seems like actual content (longer chunks without typical CSS/HTML patterns)
  const lines = text.split(/\s{2,}|[\n\r]+/);
  const contentLines = lines.filter(line => {
    // Skip short lines that might be CSS selectors or HTML tags
    if (line.length < 10) return false;

    // Keep lines that look like natural text
    return !/^[.#@][a-zA-Z0-9_-]/.test(line) && // Not starting with . # or @
           !line.includes('{') && 
           !line.includes('}') &&
           !/^[a-z]+\s[a-z]+\s[a-z]+$/.test(line); // Not just three words which might be selector
  });

  return contentLines.join(' ').trim();
}

// Process the incoming items
return processData($input.all());

