// Function to process the news data and create a summary
function processData(items) {
  try {
    // Initialize the summary string
    let summary = "# AI NEWS SUMMARY\n\n";
    let foundItems = false;

    // Process each item in the input
    for (const item of items) {
      // Access the output property directly in the json object
      if (item.json && item.json.output) {
        foundItems = true;

        // If output is a string (not an array), add it directly to the summary
        if (typeof item.json.output === 'string') {
          // Add the output content to our summary
          if (item.json.output === "NO RELEVANT AI NEWS FOUND") {
            // Handle the "NO RELEVANT AI NEWS FOUND" case specially
            summary += "## No Relevant News\n";
            summary += "No relevant AI news was found for this query.\n\n";
          } else {
            // Add the regular output
            summary += item.json.output + "\n\n";
          }
        } 
        // Handle the case where output is an array (from your previous structure)
        else if (Array.isArray(item.json.output)) {
          // Loop through each news item in the output array
          for (const newsItem of item.json.output) {
            if (newsItem.newsSumary) {
              // Add the news summary to our string
              summary += `## News Item\n${newsItem.newsSumary}\n\n`;

              // Check if there are links and add them
              if (newsItem.links && Array.isArray(newsItem.links)) {
                summary += "### Sources:\n";
                for (const link of newsItem.links) {
                  summary += `- ${link}\n`;
                }
                summary += "\n";
              }
            }
          }
        }
      }
    }

    // If no news items were found, add a note
    if (!foundItems) {
      summary += "No news items found in the provided data.\n";
    }

    // Return the summary in the correct format for n8n
    return [{
      json: {
        summary: summary
      }
    }];

  } catch (error) {
    // Handle any errors
    return [{
      json: {
        summary: "Error processing data: " + error.message,
        error: true
      }
    }];
  }
}

// Call the function with the input data
return processData($input.all());
