// This function will be executed for each item in the input
return items.map(item => {
    // Access the 'data' field in the current item
    const data = item.json.data;

    // Regular expression to match YouTube API timed text URLs
    const regex = /(https:\/\/www\.youtube\.com\/api\/timedtext[\S][^"]*)/g;

    // Find all matches in the data string
    const matches = data.match(regex);

    // Return the original item with additional matches field
    return {
        json: {
            ...item.json,
            matches: matches || [] // Add matches to the item or an empty array if no matches found
        }
    };
});

