  <div class="container">
    <h2>Error in Workflow Execution</h2>

    <div class="section">
      <h3>Workflow:</h3>
      <p>{{ $json.workflow.name }}</p>
    </div>

    <div class="section">
      <h3>Date|Time:</h3>
      <p>{{ $now }}</p>
    </div>

    <div class="section">
      <h3>Last Node Executed:</h3>
      <p>{{ $json.execution.lastNodeExecuted }}</p>
    </div>

    <div class="section">
      <h3>Error Detail:</h3>
      <p>{{ $json.execution.error.message }}</p>
    </div>

    <div class="section">
      <h3>Error Stack:</h3>
      <div class="error-stack">{{ $json.execution.error.stack }}</div>
    </div>

    <div class="footer">
      <p>Generated by n8n</p>
    </div>
  </div>
</body>
</html>
