// Advanced DateTime Tool for n8n Agent with support for business timeframes
module.exports = async function() {
  // Create date objects
  const now = new Date();

  // Basic date components
  const year = now.getFullYear();
  const month = now.getMonth(); // 0-based
  const day = now.getDate();
  const dayOfWeek = now.getDay(); // 0 = Sunday, 6 = Saturday

  // Format helper function
  const formatDate = (date) => {
    return {
      formatted: `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')}`,
      year: date.getFullYear(),
      month: date.getMonth() + 1,
      day: date.getDate()
    };
  };

  // Get start of current day
  const startOfDay = new Date(year, month, day);
  startOfDay.setHours(0, 0, 0, 0); // Set to beginning of the day

  // Get start of current month
  const startOfMonth = new Date(year, month, 1);
  startOfMonth.setHours(0, 0, 0, 0); // Set to beginning of the day

  // Get start of current year
  const startOfYear = new Date(year, 0, 1);
  startOfYear.setHours(0, 0, 0, 0); // Set to beginning of the day

  // Get start of current week (Sunday-based)
  const startOfWeek = new Date(now);
  startOfWeek.setDate(now.getDate() - now.getDay());
  startOfWeek.setHours(0, 0, 0, 0);

  // Month-to-Date (MTD)
  const mtdDays = day;
  const mtdStart = formatDate(startOfMonth);
  const mtdEnd = formatDate(now);

  // Year-to-Date (YTD)
  const ytdDays = Math.floor((now - startOfYear) / (1000 * 60 * 60 * 24)) + 1;
  const ytdStart = formatDate(startOfYear);
  const ytdEnd = formatDate(now);

  // Last month (for Month-over-Month)
  let lastMonthYear = year;
  let lastMonthMonth = month - 1;

  // Adjust for January (handle previous year December)
  if (lastMonthMonth < 0) {
    lastMonthMonth = 11; // December
    lastMonthYear = year - 1;
  }

  const lastMonth = new Date(lastMonthYear, lastMonthMonth, 1);
  const lastMonthEnd = new Date(year, month, 0);
  lastMonthEnd.setHours(23, 59, 59, 999); // Set to end of the day
  const lastMonthName = new Intl.DateTimeFormat('en-US', { month: 'long' }).format(lastMonth);

  // Last year (for Year-over-Year)
  const lastYear = new Date(year - 1, month, day);
  const lastYearStart = new Date(year - 1, 0, 1);
  lastYearStart.setHours(0, 0, 0, 0); // Set to beginning of the day
  const lastYearEnd = new Date(year - 1, 11, 31);
  lastYearEnd.setHours(23, 59, 59, 999); // Set to end of the day

  // Format current time
  const hours = String(now.getHours()).padStart(2, '0');
  const minutes = String(now.getMinutes()).padStart(2, '0');
  const seconds = String(now.getSeconds()).padStart(2, '0');
  const timeString = `${hours}:${minutes}:${seconds}`;

  // Calculate days in the current year (accounting for leap years)
  const isLeapYear = (year % 4 === 0 && year % 100 !== 0) || (year % 400 === 0);
  const daysInYear = isLeapYear ? 366 : 365;

  // Safely handle day overflow for last year (e.g., Feb 29 on leap year to non-leap year)
  function createSafePreviousYearDate(currentYear, currentMonth, currentDay) {
    // Check if we're dealing with Feb 29 on a leap year
    if (currentMonth === 1 && currentDay === 29) { // February 29
      const prevYear = currentYear - 1;
      const isPrevLeapYear = (prevYear % 4 === 0 && prevYear % 100 !== 0) || (prevYear % 400 === 0);

      if (!isPrevLeapYear) {
        // Previous year is not a leap year, so Feb 29 doesn't exist - use Feb 28 instead
        return new Date(prevYear, currentMonth, 28);
      }
    }

    // For all other cases, just use the same month and day in the previous year
    return new Date(currentYear - 1, currentMonth, currentDay);
  }

  // Recalculate lastYear date to handle potential February 29 issue
  const safeLastYear = createSafePreviousYearDate(year, month, day);

  // Return comprehensive date/time information
  const result = {
    current: {
      timestamp: now.getTime(),
      dateTime: `${formatDate(now).formatted} ${timeString}`,
      date: formatDate(now).formatted,
      time: timeString,
      year: year,
      month: month + 1,
      monthName: new Intl.DateTimeFormat('en-US', { month: 'long' }).format(now),
      day: day,
      dayOfWeek: dayOfWeek,
      dayName: new Intl.DateTimeFormat('en-US', { weekday: 'long' }).format(now),
      quarter: Math.floor(month / 3) + 1
    },

    timeframes: {
      // This week
      thisWeek: {
        start: formatDate(startOfWeek),
        end: formatDate(now),
        daysPassed: dayOfWeek,
        daysRemaining: 6 - dayOfWeek
      },

      // This month
      thisMonth: {
        start: formatDate(startOfMonth),
        end: formatDate(now),
        name: new Intl.DateTimeFormat('en-US', { month: 'long' }).format(now),
        daysPassed: day - 1,
        totalDays: new Date(year, month + 1, 0).getDate()
      },

      // This year
      thisYear: {
        start: formatDate(startOfYear),
        end: formatDate(now),
        year: year,
        daysPassed: ytdDays,
        totalDays: daysInYear
      },

      // Month-to-Date (MTD)
      mtd: {
        start: mtdStart,
        end: mtdEnd,
        daysPassed: mtdDays,
        percentComplete: Math.round((mtdDays / new Date(year, month + 1, 0).getDate()) * 100)
      },

      // Year-to-Date (YTD)
      ytd: {
        start: ytdStart,
        end: ytdEnd,
        daysPassed: ytdDays,
        percentComplete: Math.round((ytdDays / daysInYear) * 100)
      }
    },

    comparisons: {
      // Month-over-Month (MoM)
      mom: {
        current: {
          month: month + 1,
          monthName: new Intl.DateTimeFormat('en-US', { month: 'long' }).format(now),
          year: year
        },
        previous: {
          month: lastMonth.getMonth() + 1,
          monthName: lastMonthName,
          year: lastMonth.getFullYear()
        },
        start: formatDate(lastMonth),
        end: formatDate(lastMonthEnd)
      },

      // Year-over-Year (YoY)
      yoy: {
        current: {
          year: year,
          month: month + 1,
          day: day
        },
        previous: {
          year: safeLastYear.getFullYear(),
          month: safeLastYear.getMonth() + 1,
          day: safeLastYear.getDate()
        },
        sameDay: formatDate(safeLastYear),
        yearStart: formatDate(lastYearStart),
        yearEnd: formatDate(lastYearEnd)
      }
    },

    timezone: {
      name: Intl.DateTimeFormat().resolvedOptions().timeZone,
      offset: now.getTimezoneOffset()
    },

    // Business days calculation (Mon-Fri)
    business: {
      isBusinessDay: (dayOfWeek >= 1 && dayOfWeek <= 5),
      daysSinceLastBusinessDay: dayOfWeek === 0 ? 2 : dayOfWeek === 1 ? 3 : 1,
      businessDaysInMonth: getBusinessDaysInMonth(year, month),
      businessDaysPassedThisMonth: getBusinessDaysPassed(year, month, day)
    }
  };

  // Helper function to calculate business days in a month (Mon-Fri)
  function getBusinessDaysInMonth(year, month) {
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    let businessDays = 0;

    for (let i = 1; i <= daysInMonth; i++) {
      const dayOfWeek = new Date(year, month, i).getDay();
      if (dayOfWeek >= 1 && dayOfWeek <= 5) {
        businessDays++;
      }
    }

    return businessDays;
  }

  // Helper function to calculate business days passed in the current month
  function getBusinessDaysPassed(year, month, currentDay) {
    let businessDays = 0;

    for (let i = 1; i <= currentDay; i++) {
      const dayOfWeek = new Date(year, month, i).getDay();
      if (dayOfWeek >= 1 && dayOfWeek <= 5) {
        businessDays++;
      }
    }

    return businessDays;
  }

  return JSON.stringify(result);
};
