// Advanced DateTime Tool for n8n Agent with support for business timeframes
module.exports = async function() {
  // Create date objects
  const now = new Date();

  // Basic date components
  const year = now.getFullYear();
  const month = now.getMonth(); // 0-based
  const day = now.getDate();
  const dayOfWeek = now.getDay(); // 0 = Sunday, 6 = Saturday

  // Format helper function
  const formatDate = (date) => {
    return {
      formatted: `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')}`,
      year: date.getFullYear(),
      month: date.getMonth() + 1,
      day: date.getDate()
    };
  };

  // Get start of current day
  const startOfDay = new Date(year, month, day);

  // Get start of current month
  const startOfMonth = new Date(year, month, 1);

  // Get start of current year
  const startOfYear = new Date(year, 0, 1);

  // Get start of current week (Sunday-based)
  const startOfWeek = new Date(now);
  startOfWeek.setDate(now.getDate() - now.getDay());
  startOfWeek.setHours(0, 0, 0, 0);

  // Month-to-Date (MTD)
  const mtdDays = day;
  const mtdStart = formatDate(startOfMonth);
  const mtdEnd = formatDate(now);

  // Year-to-Date (YTD)
  const ytdDays = Math.floor((now - startOfYear) / (1000 * 60 * 60 * 24)) + 1;
  const ytdStart = formatDate(startOfYear);
  const ytdEnd = formatDate(now);

  // Last month (for Month-over-Month)
  const lastMonth = new Date(year, month - 1, 1);
  const lastMonthEnd = new Date(year, month, 0);
  const lastMonthName = new Intl.DateTimeFormat('en-US', { month: 'long' }).format(lastMonth);

  // Last year (for Year-over-Year)
  const lastYear = new Date(year - 1, month, day);
  const lastYearStart = new Date(year - 1, 0, 1);
  const lastYearEnd = new Date(year - 1, 11, 31);

  // Format current time
  const hours = String(now.getHours()).padStart(2, '0');
  const minutes = String(now.getMinutes()).padStart(2, '0');
  const seconds = String(now.getSeconds()).padStart(2, '0');
  const timeString = `${hours}:${minutes}:${seconds}`;

  // Return comprehensive date/time information
  return {
    current: {
      timestamp: now.getTime(),
      dateTime: `${formatDate(now).formatted} ${timeString}`,
      date: formatDate(now).formatted,
      time: timeString,
      year: year,
      month: month + 1,
      monthName: new Intl.DateTimeFormat('en-US', { month: 'long' }).format(now),
      day: day,
      dayOfWeek: dayOfWeek,
      dayName: new Intl.DateTimeFormat('en-US', { weekday: 'long' }).format(now),
      quarter: Math.floor(month / 3) + 1
    },

    timeframes: {
      // This week
      thisWeek: {
        start: formatDate(startOfWeek),
        end: formatDate(now),
        daysPassed: dayOfWeek,
        daysRemaining: 6 - dayOfWeek
      },

      // This month
      thisMonth: {
        start: formatDate(startOfMonth),
        end: formatDate(now),
        name: new Intl.DateTimeFormat('en-US', { month: 'long' }).format(now),
        daysPassed: day - 1,
        totalDays: new Date(year, month + 1, 0).getDate()
      },

      // This year
      thisYear: {
        start: formatDate(startOfYear),
        end: formatDate(now),
        year: year,
        daysPassed: ytdDays,
        totalDays: (new Date(year, 11, 31) - new Date(year, 0, 1)) / (1000 * 60 * 60 * 24) + 1
      },

      // Month-to-Date (MTD)
      mtd: {
        start: mtdStart,
        end: mtdEnd,
        daysPassed: mtdDays,
        percentComplete: Math.round((mtdDays / new Date(year, month + 1, 0).getDate()) * 100)
      },

      // Year-to-Date (YTD)
      ytd: {
        start: ytdStart,
        end: ytdEnd,
        daysPassed: ytdDays,
        percentComplete: Math.round((ytdDays / 365) * 100)
      }
    },

    comparisons: {
      // Month-over-Month (MoM)
      mom: {
        current: {
          month: month + 1,
          monthName: new Intl.DateTimeFormat('en-US', { month: 'long' }).format(now),
          year: year
        },
        previous: {
          month: lastMonth.getMonth() + 1,
          monthName: lastMonthName,
          year: lastMonth.getFullYear()
        },
        start: formatDate(lastMonth),
        end: formatDate(lastMonthEnd)
      },

      // Year-over-Year (YoY)
      yoy: {
        current: {
          year: year,
          month: month + 1,
          day: day
        },
        previous: {
          year: year - 1,
          month: month + 1,
          day: day
        },
        sameDay: formatDate(lastYear),
        yearStart: formatDate(lastYearStart),
        yearEnd: formatDate(lastYearEnd)
      }
    },

    timezone: {
      name: Intl.DateTimeFormat().resolvedOptions().timeZone,
      offset: now.getTimezoneOffset()
    }
  };
};
