// Query templates with parameterized SQL
const queryTemplates = {
  'price_adjustments': `
    SELECT 
        d.Name AS DealerName,
        AVG(CAST(ISNULL(l.PriceChanges, 0) AS FLOAT)) AS AvgPriceAdjustments,
        COUNT_BIG(*) AS TotalSales
    FROM 
