// Get the user's question and current date
const userQuestion = $input.item.json.chatinput || "";
const now = new Date();

// Detect time period and convert to actual dates
function getDateRange(timePeriod) {
  let startDate, endDate = new Date(now);

  if (timePeriod.includes('last day')) {
    startDate = new Date(now);
    startDate.setDate(startDate.getDate() - 1);
  } else if (timePeriod.includes('last week')) {
    startDate = new Date(now);
    startDate.setDate(startDate.getDate() - 7);
  } else if (timePeriod.includes('last month')) {
    startDate = new Date(now.getFullYear(), now.getMonth() - 1, 1);
    endDate = new Date(now.getFullYear(), now.getMonth(), 0);
  } else if (timePeriod.includes('MTD')) {
    startDate = new Date(now.getFullYear(), now.getMonth(), 1);
  } else if (timePeriod.includes('YTD')) {
    startDate = new Date(now.getFullYear(), 0, 1);
  } else if (timePeriod.includes('MOM')) {
    // Month over Month (current month vs previous month)
    startDate = new Date(now.getFullYear(), now.getMonth() - 1, 1);
    endDate = new Date(now.getFullYear(), now.getMonth(), 0);
  } else if (timePeriod.includes('YOY')) {
    // Year over Year (current month this year vs same month last year)
    startDate = new Date(now.getFullYear() - 1, now.getMonth(), 1);
    endDate = new Date(now.getFullYear() - 1, now.getMonth() + 1, 0);
  } else if (timePeriod.includes('last 90 days')) {
    startDate = new Date(now);
    startDate.setDate(startDate.getDate() - 90);
  } else if (timePeriod.includes('last 1Y')) {
    startDate = new Date(now);
    startDate.setFullYear(startDate.getFullYear() - 1);
  } else if (timePeriod.includes('last 3Y')) {
    startDate = new Date(now);
    startDate.setFullYear(startDate.getFullYear() - 3);
  } else if (timePeriod.includes('last 5Y')) {
    startDate = new Date(now);
    startDate.setFullYear(startDate.getFullYear() - 5);
  } else if (timePeriod.includes('last year')) {
    startDate = new Date(now.getFullYear() - 1, 0, 1);
    endDate = new Date(now.getFullYear() - 1, 11, 31);
  } else {
    // Default to last 30 days
    startDate = new Date(now);
    startDate.setDate(startDate.getDate() - 30);
  }

  return {
    start_date: startDate.toISOString().split('T')[0],
    end_date: endDate.toISOString().split('T')[0]
  };
}

// Helper function for comparison clauses
function getComparisonClauses(comparisonGroup, dealerMainBrand) {
  if (comparisonGroup === 'brand') {
    return {
      select: "d.Name AS ComparisonDealer",
      where: `AND l.SpecMake = '${dealerMainBrand}'`,
      group: "d.Name"
    };
  } else if (comparisonGroup === 'non-brand') {
    return {
      select: "d.Name AS ComparisonDealer",
      where: `AND l.SpecMake <> '${dealerMainBrand}'`,
      group: "d.Name"
    };
  } else {
    return {
      select: "'All Dealers' AS ComparisonGroup",
      where: "",
      group: "'All Dealers'"
    };
  }
}

// Try to match the question to a template
let matchedTemplate = null;
let templateParams = {};

for (const matcher of templateMatchers) {
  const matches = userQuestion.match(matcher.pattern);
  if (matches) {
    matchedTemplate = matcher.templateName;
    templateParams = matcher.extractParams(matches, userQuestion);

    // Add date params based on detected time period
    const dateRange = getDateRange(templateParams.time_period || "last month");
    templateParams.start_date = dateRange.start_date;
    templateParams.end_date = dateRange.end_date;

    // Special handling for comparison queries
    if (matchedTemplate === 'inventory_turn_rate_comparison') {
      const mainBrand = "Toyota"; // This should be retrieved dynamically in production
      const clauses = getComparisonClauses(templateParams.comparison_group, mainBrand);
      templateParams.comparison_select_clause = clauses.select;
      templateParams.comparison_where_clause = clauses.where;
      templateParams.comparison_group_clause = clauses.group;
    }

    break;
  }
}

// If we matched a template, populate it with parameters
if (matchedTemplate && queryTemplates[matchedTemplate]) {
  let query = queryTemplates[matchedTemplate];

  // Replace template parameters
  Object.keys(templateParams).forEach(key => {
    query = query.replace(new RegExp(`{{${key}}}`, 'g'), templateParams[key]);
  });

  // Output the populated template
  return { 
    template_matched: true,
    template_name: matchedTemplate,
    template_params: templateParams,
    query: query.trim(),
    ...templateParams,
    original_input: $input.item.json
  };
} else {
  // No template matched, continue with normal LLM-based query generation
  return { 
    template_matched: false,
    query: null,
    original_input: $input.item.json
  };
}

// Get the SQL query from the previous node
const sqlQuery = $json["query"];

// Basic validation to prevent dangerous queries
const disallowedPatterns = [
  /;\\s*--/, // End of statement with comment
  /DROP\\s+TABLE/i,
  /DELETE\\s+FROM/i,
  /UPDATE\\s+/i,
  /INSERT\\s+INTO/i,
  /ALTER\\s+TABLE/i,
];

// Check for dangerous patterns
for (const pattern of disallowedPatterns) {
  if (pattern.test(sqlQuery)) {
    throw new Error("Generated SQL query contains disallowed operations.");
  }
}

// Validate proper JOIN conditions
if (sqlQuery.includes("JOIN") && !sqlQuery.includes("ON ")) {
  throw new Error("JOIN without proper ON clause detected.");
}

// Validate ORDER BY clause for TOP queries
if (sqlQuery.includes("TOP ") && !sqlQuery.includes("ORDER BY")) {
  throw new Error("TOP query without ORDER BY clause detected.");
}

// Validate date range calculations
if (sqlQuery.includes("DATEADD") || sqlQuery.includes("DATEDIFF")) {
  if (!sqlQuery.includes("IS NOT NULL")) {
    console.log("Warning: Date calculation without NULL check detected.");
  }
}

// If safe, pass the SQL query along
return { query: sqlQuery };

// Get start and stop times
const startTime = $('Template or LLM').first()?.json?.original_input?.Start_time;
const stopTime = new Date($input.first().json.stopTime); // Convert to Date object

// Calculate the difference in milliseconds
const deltaMs = stopTime - startTime;

// Convert to seconds (with 2 decimal places)
const deltaSeconds = (deltaMs / 1000).toFixed(2);

// Convert to a more readable format if it's a longer duration
let readableDelta;
if (deltaMs < 1000) {
  readableDelta = `${deltaMs} ms`;
} else if (deltaMs < 60000) {
  readableDelta = `${deltaSeconds} seconds`;
} else {
  const minutes = Math.floor(deltaMs / 60000);
  const seconds = ((deltaMs % 60000) / 1000).toFixed(2);
  readableDelta = `${minutes} minutes and ${seconds} seconds`;
}

// Return both the original data and the time measurements
return {
  ...($input.item.json),
  stopTime: new Date(stopTime).toISOString(),
  executionTimeMs: deltaMs,
  executionTimeSec: parseFloat(deltaSeconds),
  executionTimeReadable: readableDelta
};
