// Advanced DateTime Tool for n8n
module.exports = function() {
  // Create date objects
  const now = new Date();

  // Basic date components
  const year = now.getFullYear();
  const month = now.getMonth(); // 0-based
  const day = now.getDate();

  // Format date as string
  const dateString = `${year}-${String(month + 1).padStart(2, '0')}-${String(day).padStart(2, '0')}`;

  // Format time
  const hours = String(now.getHours()).padStart(2, '0');
  const minutes = String(now.getMinutes()).padStart(2, '0');
  const seconds = String(now.getSeconds()).padStart(2, '0');
  const timeString = `${hours}:${minutes}:${seconds}`;

  // Create simple data object
  const data = {
    current: {
      dateTime: `${dateString} ${timeString}`,
      date: dateString,
      time: timeString
    }
  };

  // Create the response property n8n expects
  const responseData = {
    response: JSON.stringify(data)
  };

  // Debug
  console.log("Response data:", responseData);
  console.log("Response type:", typeof responseData.response);

  // Return the object with the response property containing a string
  return responseData;
};

