// Check if there are any results
if (queryResults.length === 0) {
  return [{ json: { textOutput: "No results found for your query." } }];
}

// Extract column names (headers)
const headers = Object.keys(queryResults[0]);

// Initialize an array to hold formatted lines
let lines = [];

// Create a header row (optional)
lines.push(headers.join(' | '));
lines.push(headers.map(() => '---').join(' | ')); // For Markdown table formatting

// Iterate over each result item
queryResults.forEach(item => {
  const row = headers.map(header => item[header]);
  lines.push(row.join(' | '));
});

// Join all lines into a single string with line breaks
const textOutput = lines.join('\n');

// Return the formatted text as a new field
return [{ json: { textOutput } }];
