// Detect time period and convert to actual dates
function getDateRange(timePeriod) {
  let startDate, endDate = new Date(now);

  if (timePeriod.includes('last day')) {
    startDate = new Date(now);
    startDate.setDate(startDate.getDate() - 1);
  } else if (timePeriod.includes('last week')) {
    startDate = new Date(now);
    startDate.setDate(startDate.getDate() - 7);
  } else if (timePeriod.includes('last month')) {
    startDate = new Date(now.getFullYear(), now.getMonth() - 1, 1);
    endDate = new Date(now.getFullYear(), now.getMonth(), 0);
  } else if (timePeriod.includes('MTD')) {
    startDate = new Date(now.getFullYear(), now.getMonth(), 1);
  } else if (timePeriod.includes('YTD')) {
    startDate = new Date(now.getFullYear(), 0, 1);
  } else if (timePeriod.includes('MOM')) {
    // Month over Month (current month vs previous month)
    startDate = new Date(now.getFullYear(), now.getMonth() - 1, 1);
    endDate = new Date(now.getFullYear(), now.getMonth(), 0);
  } else if (timePeriod.includes('YOY')) {
    // Year over Year (current month this year vs same month last year)
    startDate = new Date(now.getFullYear() - 1, now.getMonth(), 1);
    endDate = new Date(now.getFullYear() - 1, now.getMonth() + 1, 0);
  } else if (timePeriod.includes('last 90 days')) {
    startDate = new Date(now);
    startDate.setDate(startDate.getDate() - 90);
  } else if (timePeriod.includes('last 1Y')) {
    startDate = new Date(now);
    startDate.setFullYear(startDate.getFullYear() - 1);
  } else if (timePeriod.includes('last 3Y')) {
    startDate = new Date(now);
    startDate.setFullYear(startDate.getFullYear() - 3);
  } else if (timePeriod.includes('last 5Y')) {
    startDate = new Date(now);
    startDate.setFullYear(startDate.getFullYear() - 5);
  } else if (timePeriod.includes('last year')) {
    startDate = new Date(now.getFullYear() - 1, 0, 1);
    endDate = new Date(now.getFullYear() - 1, 11, 31);
  } else {
    // Default to last 30 days
    startDate = new Date(now);
    startDate.setDate(startDate.getDate() - 30);
  }

  return {
    start_date: startDate.toISOString().split('T')[0],
    end_date: endDate.toISOString().split('T')[0]
  };
}

// Helper function for comparison clauses
