// Debugging to see if nodes have been executed and data is available
console.log('Positive Node Data:', $('Positive').first());
console.log('Negative Node Data:', $('Negative').first());
console.log('Neutral Node Data:', $('Neutral').first());

// Get the counts from input items
const positive = $('Positive').first()?.json.count_sentimentAnalysis || 0;
const negative = $('Negative').first()?.json.count_sentimentAnalysis || 0; 
const neutral = $('Neutral').first()?.json.count_sentimentAnalysis || 0;

// Calculate total comments
const total_comments = positive + neutral + negative;
if (total_comments === 0) {
  return { json: { snps: 0, details: { positive_percent: "0%", negative_percent: "0%", total_comments: total_comments } } };
}

const percentPositive = (positive / total_comments) * 100;
const percentNegative = (negative / total_comments) * 100;
const snps = (percentPositive - percentNegative).toFixed(2);

return {
  json: {
    snps: snps,
    details: {
      positive_percent: percentPositive.toFixed(2) + '%',
      negative_percent: percentNegative.toFixed(2) + '%',
      total_comments: total_comments
    }
  }
};

