// Get input data from previous node
const items = $input.all();

// Create properly formatted records for Airtable
const formattedRecords = items.map(item => {
  const data = item.json;

  return {
    // The Airtable record ID - assuming 'vid_id' is your record ID field
    id: data.vid_id,

    // All fields wrapped in a "fields" object
    fields: {
      // Only include fields that exist and have values to avoid errors
      // Primary fields
      ...(data.channelName && { channelName: data.channelName }),
      ...(data.channelURL && { channelURL: data.channelURL }),
      ...(data.commentCount !== undefined && { commentCount: data.commentCount }),
      ...(data.Comments && { Comments: data.Comments }),
      ...(data.contentType && { contentType: data.contentType }),
      ...(data.dateProcessed && { dateProcessed: data.dateProcessed }),
      ...(data.Description && { Description: data.Description }),
      ...(data.docURL && { docURL: data.docURL }),
      ...(data.Duration && { Duration: data.Duration }),
      ...(data.hashtags && { hashtags: data.hashtags }),
      ...(data.Likes !== undefined && { Likes: data.Likes }),
      ...(data.negative !== undefined && { negative: data.negative }),
      ...(data.neutral !== undefined && { neutral: data.neutral }),
      ...(data.positive !== undefined && { positive: data.positive }),
      ...(data.processVideo !== undefined && { processVideo: data.processVideo }),
      ...(data.publishDate && { publishDate: data.publishDate }),
      ...(data.Status && { Status: data.Status }),
      ...(data.summary && { summary: data.summary }),
      ...(data.thumbnailUrl && { thumbnailUrl: data.thumbnailUrl }),
      ...(data.Title && { Title: data.Title }),
      ...(data.transcriptURL && { transcriptURL: data.transcriptURL }),
      ...(data.videoURL && { videoURL: data.videoURL }),
      ...(data.viewCount !== undefined && { viewCount: data.viewCount }),

      // Link fields require special formatting in Airtable
      ...(data.channel && { channel: Array.isArray(data.channel) ? data.channel : [data.channel] }),
      ...(data.Comments2 && { Comments2: Array.isArray(data.Comments2) ? data.Comments2 : [data.Comments2] })
    }
  };
});

// Ensure we're only sending up to 10 records per batch (Airtable's limit)
// Split into batches if necessary
const BATCH_SIZE = 10;
const batches = [];

for (let i = 0; i < formattedRecords.length; i += BATCH_SIZE) {
  batches.push(formattedRecords.slice(i, i + BATCH_SIZE));
}

// FIX: Return with proper json object structure
// This is the key change - we're returning an object with a json property that contains the records
if (batches.length > 0) {
  console.log(`Formatted ${formattedRecords.length} records into ${batches.length} batches`);
  // Return the data correctly formatted
  return {
    json: {
      records: batches[0]
    }
  };
} else {
  // Return an empty but valid object if there are no records to update
  return {
    json: {
      records: []
    }
  };
}

// Initialize counters for each sentiment category
let positiveCount = 0;
let neutralCount = 0;
let negativeCount = 0;

// Get the data from the Aggregate node
const aggregateData = $input.first().json;

// Check if we have data array
if (aggregateData && aggregateData.data && Array.isArray(aggregateData.data)) {
  const dataItems = aggregateData.data;
  const totalComments = dataItems.length;

  // Loop through each data item
  dataItems.forEach(item => {
    // Access the sentiment category from the sentimentAnalysis field
    if (item.sentimentAnalysis && item.sentimentAnalysis.category) {
      const category = item.sentimentAnalysis.category.toLowerCase();

      // Count based on sentiment category
      if (category.includes('positive')) {
        positiveCount++;
      } else if (category.includes('negative')) {
        negativeCount++;
      } else if (category.includes('neutral')) {
        neutralCount++;
      }
    }
  });

  // Calculate percentages
  const percentPositive = totalComments > 0 ? (positiveCount / totalComments * 100).toFixed(1) : 0;
  const percentNeutral = totalComments > 0 ? (neutralCount / totalComments * 100).toFixed(1) : 0;
  const percentNegative = totalComments > 0 ? (negativeCount / totalComments * 100).toFixed(1) : 0;

  // Return the summary
  return {
    json: {
      summary: {
        totalComments,
        positive: {
          count: positiveCount,
          percentage: parseFloat(percentPositive)
        },
        neutral: {
          count: neutralCount,
          percentage: parseFloat(percentNeutral)
        },
        negative: {
          count: negativeCount,
          percentage: parseFloat(percentNegative)
        }
      }
    }
  };
} else {
  // Handle case where there's no data
  return {
    json: {
      summary: {
        totalComments: 0,
        positive: { count: 0, percentage: 0 },
        neutral: { count: 0, percentage: 0 },
        negative: { count: 0, percentage: 0 }
      }
    }
  };
}
